import { combineReducers } from 'redux'
import { createReducer } from 'redux-create-reducer';

export const TYPES = {
  PING: 'PING',
  PONG: 'PONG'
}

const initialState = {
  text: 'Use Redux',
  completed: false,
  id: 0
}

export const ping = () => ({ type: TYPES.PING, isPinging: true })

export const pong = () => ({ isPinging: false })

