import React from 'react';
import './AppContainer.css';
import MainScreen from './Containers/MainScreen'

function AppContainer() {
  function isPalindrome(word) {
    if (word.length < 2) return false
    const lowerCaseWord = word.toLowerCase()
    const forwardArr = word.toLowerCase().split('')
    const backwardArr = lowerCaseWord.reverse()
    console.log(backwardArr)
    for (let i = 0; i < word.length; i += 1) {
      console.log(`${forwardArr[i]} ${backwardArr[i]}`)

      if (backwardArr[i] != forwardArr[i]) {
        return false
      }
    }
    return true
  }
  // function fruitPresent(arr, fruitName) {
  //   const lowerCaseFruit = fruitName.toLowerCase()
  //   return arr.map(fruit => fruit.toLowerCase() === lowerCaseFruit);
  // }
  const fruits = ['Apple', 'Apricot', 'Avocado', 'banana']
  return (
    <div className="App">
      {/* {console.log(fruitPresent(fruits, 'Apple'))} */}
      {console.log(isPalindrome('fruits'))}
      <MainScreen />
    </div>
  );
}

export default AppContainer;
