import { ofType } from 'redux-observable'
import { map } from 'rxjs/operators';
import { dispatch } from 'rxjs/internal/observable/pairs';
import { TYPES as UserTypes } from '../Redux/UserRedux'
import { TYPES as ErrorTypes } from '../Redux/ErrorRedux'
import firebase from '../Firebase/index'

const setError = (errorMessage) => {
  console.log('FUNC')
  return (dispatch) => {
    dispatch({ type: ErrorTypes.SET_ERROR, payload: errorMessage })
  }
}

export const signUpEpic = action$ => action$.pipe(
  ofType(UserTypes.SIGN_UP),
  map(action => {
    const { email } = action.payload
    const { password } = action.payload
    // return { type: ErrorTypes.SET_ERROR, payload: 'ERROR' }
    firebase.auth().createUserWithEmailAndPassword(email, password).then(() => {
      console.log('here')
      return { type: '' }
    }).catch((error) => {
      // Handle Errors here.
      const errorCode = error.code
      const errorMessage = error.message
      console.log("error")
      const action = {
        payload: errorMessage
      }
      setError(errorMessage)
    })
    return { type: '' }
  })
)

export default null
