import { ofType } from 'redux-observable'
import { map, switchMap } from 'rxjs/operators';
import { TYPES as DashboardTypes } from '../Redux/DashboardRedux'
import { TYPES as ErrorTypes } from '../Redux/ErrorRedux'
import firebase from '../Firebase/index'

export const uploadTimesheetEpic = (action$, state$) => action$.pipe(
  ofType(DashboardTypes.UPLOAD_TIMESHEET),
  map(action => {
    const storageRef = firebase.storage().ref().child(state$.value.UserReducers.userReducer.id)
    console.log(typeof action.payload)
    return storageRef.put(new File(action.payload)).then((snapshot) => {
      return { type: '' }
    })
  })
)

export const setTimesheetFileError = (action$) => action$.pipe(
  ofType(DashboardTypes.SET_TIMESHEET_FILE_ERROR),
  map((action) => ({ type: ErrorTypes.SET_ERROR, payload: action.payload }))
)

export default null
