import { combineReducers } from 'redux'
import { createReducer } from 'redux-create-reducer';

export const TYPES = {
  SET_ERROR: 'SET_ERROR'
}

const initialState = {
  errorDescription: ''
}

export const setError = (action) => ( //(state = initialState, action) => (
  { type: TYPES.SET_ERROR, errorDescription: action.payload }
)

const errorReducer = createReducer(initialState, {
  SET_ERROR: setError
})

export const reducers = combineReducers({
  errorReducer
})
