import { combineReducers } from 'redux'

export const types = {
  PING: 'PING',
  PONG: 'PONG'
}

export const ping = () => ({ type: 'PING' })

export const pingReducer = (state = { isPinging: false }, action) => {
  switch (action.type) {
    case 'PING':
      console.log('PING')
      return { isPinging: true };

    case 'PONG':
      console.log('PONG')
      return { isPinging: false };

    default:
      return state;
  }
};

export const reducers = combineReducers({
  ping,
  pingReducer
})
