import {
  combineEpics,
  ofType
} from 'redux-observable'
import { mapTo, delay, map } from 'rxjs/operators'
import { TYPES as LayoutTypes } from '../Redux/LayoutRedux'

export const pingEpic = action$ => action$.pipe(
  ofType(LayoutTypes.PING),
  delay(500), // Asynchronously wait 1000ms then continue
  mapTo({ type: LayoutTypes.PONG })
)

export const pongEpic = action$ => action$.pipe(
  ofType(LayoutTypes.PONG),
  map({
    type: ''
  })
)

// eslint-disable-next-line import/prefer-default-export
export const rootEpic = combineEpics(
  pingEpic,
  pongEpic
);
