import React, { Component } from 'react'
import '../LogIn/LogInStyles.css'
// import firebase from '../../Firebase/index'

class LoginPage extends Component {
  constructor() {
    super()
    this.state = {
      username: '',
      password: '',
      error: '',
    }
  }

  dismissError = () => {
    this.setState({ error: '' })
  }

  handleSubmit = (evt) => {
    const { username, password } = this.state
    evt.preventDefault()

    if (!username) {
      return this.setState({ error: 'Username is required' })
    }

    if (!password) {
      return this.setState({ error: 'Password is required' })
    }

    return this.setState({ error: '' })
  }

  handleUserChange = (evt) => {
    this.setState({
      username: evt.target.value,
    })
  }

  handlePassChange = (evt) => {
    this.setState({
      password: evt.target.value,
    })
  }

  render() {
    // NOTE: I use data-attributes for easier E2E testing
    // but you don't need to target those (any css-selector will work)
    const { error, username, password } = this.state
    return (
      <div className="Container">
        <div className="Login">
          <form onSubmit={this.handleSubmit}>
            {
              error &&
              <h3 className="Error" data-test="error" onClick={this.dismissError}>
                <button className="XButton" type="button" onClick={this.dismissError}>âœ–</button>
                {error}
              </h3>
            }
            <div className="UserNameContainer">
              <label className="UserNameLabel">Email:</label>
              <input className="UserNameInput" type="text" data-test="username" value={username} onChange={this.handleUserChange} />
            </div>
            <div className="PasswordContainer">
              <label className="PasswordLabel">Password:</label>
              <input className="PasswordInput" type="password" data-test="password" value={password} onChange={this.handlePassChange} />
            </div>
            <input className="SubmitButton" type="submit" value="Log In" data-test="submit" />
          </form>
        </div>
      </div>
    )
  }
}

export default LoginPage
