import { combineReducers } from 'redux'

export const TYPES = {
  PING: 'PING',
  PONG: 'PONG'
}

export const ping = () => ({ type: TYPES.PING })

export const pingReducer = (state = { isPinging: false }, action) => {
  switch (action.type) {
    case TYPES.PING:
      console.log(TYPES.PING)
      return { isPinging: true };

    case TYPES.PONG:
      console.log(TYPES.PONG)
      return { isPinging: false };

    default:
      return state;
  }
};

export const reducers = combineReducers({
  ping,
  pingReducer
})
