import { ofType } from 'redux-observable'
import { map } from 'rxjs/operators';
import { TYPES as UserTypes } from '../Redux/UserRedux'
import { TYPES as ErrorTypes, setError } from '../Redux/ErrorRedux'
import firebase from '../Firebase/index'

export const signup = (email, password) => async dispatch => {
  try {
    firebase.auth().createUserWithEmailAndPassword(email, password).then(dataBeforeEmail => {
      firebase.auth().onAuthStateChanged(function(user) {
        user.sendEmailVerification();
      });
    }).then(dataAfterEmail => {
      firebase.auth().onAuthStateChanged(function(user) {
        if (user.emailVerified) {
          // // Email is verified
          // dispatch({
          //   type: SIGNUP_SUCCESS,
          //   payload:
          //     "Your account was successfully created! Now you need to verify your e-mail address, please go check your inbox."
          // });
        } else {
          // Email is not verified
          dispatch({
            type: ErrorTypes.SET_ERROR,
            payload:
              "Something went wrong, we couldn't create your account. Please try again."
          });
        }
      });
    })
      .catch(function(error) {
        dispatch({
          type: ErrorTypes.SET_ERROR,
          payload:
            "Something went wrong, we couldn't create your account. Please try again."
        });
      });
  } catch (err) {
    dispatch({
      type: ErrorTypes.SET_ERROR,
      payload:
        "Something went wrong, we couldn't create your account. Please try again."
    });
  }
}

export const signUpEpic = action$ => action$.pipe(
  ofType(UserTypes.SIGN_UP),
  map(action => {
