import { combineReducers } from 'redux'
import { createReducer } from 'redux-create-reducer';

export const TYPES = {
  PING: 'PING',
  PONG: 'PONG'
}

const initialState = {
  currentLayout: 'HOME'
}

export const ping = (state = initialState) => ({ type: TYPES.PING, ...state, currentLayout: 'HOME' })

export const pong = () => ({ isPinging: false })

