import { ofType } from 'redux-observable'
import { map, mapTo } from 'rxjs/operators';
import { TYPES as UserTypes } from '../Redux/UserRedux'
import { TYPES as ErrorTypes, setError } from '../Redux/ErrorRedux'
import firebase from '../Firebase/index'

export const signUpEpic = action$ => action$.pipe(
  ofType(UserTypes.SIGN_UP),
  map(action => {
    const { email } = action.payload
    const { password } = action.payload
    // return { type: ErrorTypes.SET_ERROR, payload: 'ERROR' }
    firebase.auth().createUserWithEmailAndPassword(email, password).catch((error) => {
      // Handle Errors here.
      if (error !== null) {
        const errorCode = error.code
        const errorMessage = error.message
        console.log("error")
        return { type: ErrorTypes.SET_ERROR, payload: errorMessage }
      }
    }).then(() => {
      console.log("3")
      return { type: '' }
    })
    console.log("uh oh")
      return { type: '' }
  })
)

export default null
