import React, { Component } from 'react'
import '../LogIn/LogInStyles.css'
// import firebase from '../../Firebase/index'

class LoginPage extends Component {
  constructor() {
    super()
    this.state = {
      name: '',
      email: '',
      password: '',
      error: '',
      company: ''
    }
  }

  dismissError = () => {
    this.setState({ error: '' })
  }

  handleSubmit = (evt) => {
    const { email, password } = this.state
    evt.preventDefault()

    if (!email) {
      return this.setState({ error: 'Email is required' })
    }

    if (!password) {
      return this.setState({ error: 'Password is required' })
    }

    return this.setState({ error: '' })
  }

  handNameChange = (evt) => {
    this.setState({
      name: evt.target.value,
    })
  }

  handleEmailChange = (evt) => {
    this.setState({
      email: evt.target.value,
    })
  }

  handlePassChange = (evt) => {
    this.setState({
      password: evt.target.value,
    })
  }

  handleCompanyChange = (evt) => {
    this.setState({
      company: evt.target.value,
    })
  }

  render() {
    // NOTE: I use data-attributes for easier E2E testing
    // but you don't need to target those (any css-selector will work)
    const { error, email, password, company, name } = this.state
    return (
      <div className="Container">
        <div className="Login">
          <form onSubmit={this.handleSubmit}>
            {
              error &&
              <h3 className="Error" data-test="error" onClick={this.dismissError}>
                <button className="XButton" type="button" onClick={this.dismissError}>âœ–</button>
                {error}
              </h3>
            }
            <div className="NameContainer">
              <label className="NameLabel">Name:</label>
              <input className="NameInput" type="text" data-test="username" value={name} onChange={this.handleNameChange} />
            </div>
            <div className="EmailContainer">
              <label className="EmailLabel">Email:</label>
              <input className="EmailInput" type="text" data-test="username" value={email} onChange={this.handleEmailChange} />
            </div>
            <div className="CompanyContainer">
              <label className="CompanyLabel">Company:</label>
              <input className="CompanyInput" type="text" data-test="username" value={company} onChange={this.handleCompanyChange} />
            </div>
            <div className="PasswordContainer">
              <label className="PasswordLabel">Password:</label>
              <input className="PasswordInput" type="password" data-test="password" value={password} onChange={this.handlePassChange} />
            </div>
            <div className="PasswordContainer2">
              <label className="PasswordLabel2">Password:</label>
              <input className="PasswordInput2" type="password" data-test="password" value={password} onChange={this.handlePassChange} />
            </div>
            <input className="SubmitButton" type="submit" value="Sign Up" data-test="submit" />
          </form>
        </div>
      </div>
    )
  }
}

export default LoginPage
