import { combineReducers } from 'redux'
import { createReducer } from 'redux-create-reducer';

export const TYPES = {
  SIGN_UP: 'SIGN_UP',
  SET_USER_DATA: 'SET_USER_DATA'
}

const initialState = {
  id: '',
  name: '',
  company: '',
  email: ''
}

// const createUserStateObject = (obj) => {

// }

// export const setUserData = (state = initialState, action) => (
//   { type: TYPES.SET_USER_DATA, ...state, ...createUserStateObject(action.data) }
// )

export const signUp = (state = initialState, { name, email, password, company }) => {
  console.log(name)
  return { type: TYPES.SIGN_UP, payload: { name, email, password, company }, ...state }
}

const userReducer = createReducer(initialState, {
  SIGN_UP: signUp
})

export const reducers = combineReducers({
  userReducer
})
