import { createReducer } from 'redux-create-reducer';

export const TYPES = {
  SIGN_UP: 'SIGN_UP',
  SET_USER_DATA: 'SET_USER_DATA'
}

const initialState = {
  id: '',
  name: '',
  company: '',
  email: ''
}

