// )

export const signUp = (name, email, password, company) => (
  { type: TYPES.SIGN_UP, payload: { name, email, password, company }, ...state }
)

const userReducer = createReducer(initialState, {
  SIGN_UP: signUp
})

export const reducers = combineReducers({
  userReducer
})
