import { combineReducers } from 'redux'
import { createReducer } from 'redux-create-reducer';

export const TYPES = {
  PING: 'PING',
  PONG: 'PONG'
}

export const ping = () => ({ type: TYPES>PING, isPinging: true })

export const pong = () => {
  console.log(TYPES.PONG)
  return { isPinging: false };
}

