import React, { Component } from 'react'
import PropTypes from 'prop-types';
import Calendar from 'react-calendar' // https://www.npmjs.com/package/react-calendar
import DatePicker from 'react-datepicker'; // https://www.npmjs.com/package/react-datepicker
import 'react-datepicker/dist/react-datepicker.css';
import ReactDataSheet from 'react-datasheet';
// Be sure to include styles at some point, probably during your bootstrapping
import 'react-datasheet/lib/react-datasheet.css'; // https://github.com/nadbm/react-datasheet

class DashboardLayout extends Component {
  constructor(props) {
    super(props)
    this.state = {
      startDate: new Date(),
      grid: [
        [{value:  1}, {value:  3}],
        [{value:  2}, {value:  4}]
      ]
    };
  }

  handleChange = date => {
    console.log(date)

    this.setState({
      startDate: date,
      grid: [
        [{value:  4 }, {value:  3}],
        [{value:  2}, {value:  4}]
      ]
    });
  };

  render() {
    return (
      <div>
        <p>DASHBOARDLAYOUT</p>
        <DatePicker
          selected={this.state.startDate}
          onChange={this.handleChange}
        />
        <Calendar
          onChange={this.handleChange}
          value={this.state.startDate}
          selectRange
        />
        <ReactDataSheet
          data={this.state.grid}
          valueRenderer={(cell) => cell.value}
          onCellsChanged={changes => {
            const grid = this.state.grid.map(row => [...row])
            changes.forEach(({cell, row, col, value}) => {
              grid[row][col] = {...grid[row][col], value}
            })
            this.setState({grid})
          }}
        />
      </div>
    )
  }
}

DashboardLayout.propTypes = {

}

export default DashboardLayout
