import React, { Component } from 'react'
import Helmet from 'react-helmet'
import PropTypes from 'prop-types';
import './DashboardLayoutStyles.css'
// import Calendar from 'react-calendar' // https://www.npmjs.com/package/react-calendar
// import DatePicker from 'react-datepicker'; // https://www.npmjs.com/package/react-datepicker
// import 'react-datepicker/dist/react-datepicker.css';
// import ReactDataSheet from 'react-datasheet';
// // Be sure to include styles at some point, probably during your bootstrapping
// import 'react-datasheet/lib/react-datasheet.css'; // https://github.com/nadbm/react-datasheet
import { FilePicker } from 'react-file-picker'
import DayPicker from 'react-day-picker';
import 'react-day-picker/lib/style.css';
import moment from 'moment';

class DashboardLayout extends Component {
  constructor(props) {
    super(props)
    this.state = {
      timesheetFile: null,
      hoverRange: undefined,
      selectedDays: []
    };
  }

  getWeekDays(weekStart) {
    const days = [weekStart];
    for (let i = 1; i < 7; i += 1) {
      days.push(
        moment(weekStart)
          .add(i, 'days')
          .toDate()
      );
    }
    return days;
  }

  getWeekRange(date) {
    return {
      from: moment(date)
        .startOf('week')
        .toDate(),
      to: moment(date)
        .endOf('week')
        .toDate(),
    };
  }

  handleDayChange = date => {
    this.setState({
      selectedDays: this.getWeekDays(this.getWeekRange(date).from),
    });
  };

  handleDayEnter = date => {
    this.setState({
      hoverRange: this.getWeekRange(date),
    });
  };

  handleDayLeave = () => {
    this.setState({
      hoverRange: undefined, // cahnge this
    });
  };

  handleWeekClick = (weekNumber, days, e) => {
    this.setState({
      selectedDays: days,
    });
  };


  renderFileName() {
    const { timesheetFile } = this.state
    return (
      <p className="FileName">{timesheetFile.name}</p>
    )
  }

  render() {
    const { setTimesheetFileError } = this.props
    const { timesheetFile, hoverRange, selectedDays } = this.state

    const daysAreSelected = selectedDays.length > 0;

    const modifiers = {
      hoverRange,
      selectedRange: daysAreSelected && {
        from: selectedDays[0],
        to: selectedDays[6],
      },
      hoverRangeStart: hoverRange && hoverRange.from,
      hoverRangeEnd: hoverRange && hoverRange.to,
      selectedRangeStart: daysAreSelected && selectedDays[0],
      selectedRangeEnd: daysAreSelected && selectedDays[6],
    };
    return (
      <div className="DashboardContainer">
        <h1>DASHBOARD LAYOUT</h1>
        <ol className="StepsList">
          <li>Select your time period</li>
          <li>Upload excel file (xlsx)</li>
          <h3>Thats it!</h3>
        </ol>
        <div className="SelectedWeekExample">
          <DayPicker
            selectedDays={selectedDays}
            showWeekNumbers
            showOutsideDays
            modifiers={modifiers}
            onDayClick={this.handleDayChange}
            onDayMouseEnter={this.handleDayEnter}
            onDayMouseLeave={this.handleDayLeave}
            onWeekClick={this.handleWeekClick}
          />
          { selectedDays.length === 7 && (
            <div>
              {moment(selectedDays[0]).format('LL')} â€“{' '}
              {moment(selectedDays[6]).format('LL')}
            </div>
          ) }

        <Helmet>
          <style>{`
            .SelectedWeekExample .DayPicker-Month {
              border-collapse: separate;
            }
            .SelectedWeekExample .DayPicker-WeekNumber {
              outline: none;
            }
            .SelectedWeekExample .DayPicker-Day {
              outline: none;
              border: 1px solid transparent;
            }
            .SelectedWeekExample .DayPicker-Day--hoverRange {
              background-color: #EFEFEF !important;
            }

            .SelectedWeekExample .DayPicker-Day--selectedRange {
              background-color: #fff7ba !important;
              border-top-color: #FFEB3B;
              border-bottom-color: #FFEB3B;
              border-left-color: #fff7ba;
              border-right-color: #fff7ba;
            }

            .SelectedWeekExample .DayPicker-Day--selectedRangeStart {
              background-color: #FFEB3B !important;
              border-left: 1px solid #FFEB3B;
            }

            .SelectedWeekExample .DayPicker-Day--selectedRangeEnd {
              background-color: #FFEB3B !important;
              border-right: 1px solid #FFEB3B;
            }

            .SelectedWeekExample .DayPicker-Day--selectedRange:not(.DayPicker-Day--outside).DayPicker-Day--selected,
            .SelectedWeekExample .DayPicker-Day--hoverRange:not(.DayPicker-Day--outside).DayPicker-Day--selected {
              border-radius: 0 !important;
              color: black !important;
            }
            .SelectedWeekExample .DayPicker-Day--hoverRange:hover {
              border-radius: 0 !important;
            }
          `}</style>
        </Helmet>
      </div>
        <FilePicker
          extensions={['md']}
          onChange={FileObject => this.setState({ timesheetFile: FileObject })}
          onError={(error) => setTimesheetFileError({ payload: error })}
        >
          <button type="submit">
            Click to upload timesheet
          </button>
        </FilePicker>
        { timesheetFile && this.renderFileName()}
      </div>
    )
  }
}

DashboardLayout.propTypes = {
  isAdmin: PropTypes.bool.isRequired,
  setTimesheetFileError: PropTypes.func.isRequired
}

export default DashboardLayout
