import { combineReducers, createReducer } from 'redux'

export const TYPES = {
  PING: 'PING',
  PONG: 'PONG'
}

const layoutReducer = createReducer([], {
  PING: ping,
  PONG: pong
})

export const ping = () => {
  console.log(TYPES.PING)
  return { isPinging: true };
}

export const pong = () => {
  console.log(TYPES.PONG)
  return { isPinging: false };
}

