import React, { Component } from 'react'
import './LogInStyles.css'

class LoginPage extends Component {
  constructor() {
    super()
    this.state = {
      username: '',
      password: '',
      error: '',
    }
  }

  dismissError = () => {
    this.setState({ error: '' })
  }

  handleSubmit = (evt) => {
    const { username, password } = this.state
    evt.preventDefault()

    if (!username) {
      return this.setState({ error: 'Username is required' })
    }

    if (!password) {
      return this.setState({ error: 'Password is required' })
    }

    return this.setState({ error: '' })
  }

  handleUserChange = (evt) => {
    this.setState({
      username: evt.target.value,
    })
  }

  handlePassChange = (evt) => {
    this.setState({
      password: evt.target.value,
    })
  }

  render() {
    // NOTE: I use data-attributes for easier E2E testing
    // but you don't need to target those (any css-selector will work)
    const { error, username, password } = this.state
    return (
      <div className="Login">
        <form onSubmit={this.handleSubmit}>
          {
            error &&
            <h3 data-test="error" onClick={this.dismissError}>
              <button type="button" onClick={this.dismissError}>âœ–</button>
              {error}
            </h3>
          }
          <div className="UserNameContainer">
            <label>User Name</label>
            <input type="text" data-test="username" value={username} onChange={this.handleUserChange} />
          </div>
          <div className="PasswordContainer">
            <label>Password</label>
            <input type="password" data-test="password" value={password} onChange={this.handlePassChange} />
          </div>
          <input className="SubmitButton" type="submit" value="Log In" data-test="submit" />
        </form>
      </div>
    )
  }
}

export default LoginPage
