import { combineReducers } from 'redux'
import { createReducer } from 'redux-create-reducer';

export const TYPES = {
  SET_ERROR: 'SET_ERROR'
}

const initialState = {
  errorDescription: ''
}

export const setError = (action) => {
  console.log(typeof action)
  console.log(action)
  return { type: TYPES.SET_ERROR, errorDescription: action.error }
}

const errorReducer = createReducer(initialState, {
  SET_ERROR: setError
})

export const reducers = combineReducers({
  errorReducer
})
